DROP TABLE IF EXISTS profession;
CREATE TABLE `profession` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(250) NOT NULL,
  `description` TEXT NULL,
  `specialityId` INT NULL,
  `main_cell_1` VARCHAR(4) NULL,
  `main_cell_2` VARCHAR(4) NULL,
  `additional_cell_1` VARCHAR(4) NULL,
  `additional_cell_2` VARCHAR(4) NULL,
  `additional_cell_3` VARCHAR(4) NULL,
  `additional_cell_4` VARCHAR(4) NULL,
  `additional_cell_5` VARCHAR(4) NULL,
  `additional_cell_6` VARCHAR(4) NULL,
  `boss_flags` INT(4) NULL,
  PRIMARY KEY (`id`),
  INDEX `fk_profession_1_idx` (`specialityId` ASC),
  CONSTRAINT `fk_profession_1`
    FOREIGN KEY (`specialityId`)
    REFERENCES `activity_type` (`id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION);


















